<fieldset class="statistics_table uptime_monitor_popup">    <h3><?php echo urldecode( $title ); ?></h3>    <table>        <tr>            <td><b>Monitor Name:</b></td>            <td><?php echo $url->url_friendly_name ?></td>        </tr>        <tr>            <td><b>Monitor URL:</b></td>            <td><?php echo $url->url_address ?></td>        </tr>        <tr>            <td><b>Monitor Type:</b></td>            <td><?php echo $monitor_types[ $url->url_monitor_type ] ?></td>        </tr>        <tr>            <td><b>Current Status:</b></td>            <td>                <?php				$last_status = $stats[0];				if ( $last_status->monitor_type == '-1' ) {					$type = $last_status->type;					echo ucfirst( $monitor_statuses[ $type ] );				} else {					$type = $last_status->monitor_type;					switch ( $type ) {						case '0':							echo 'Paused';							break;						case '1':							echo 'Started';					}				}				?>            </td>        </tr>           </table>    <?php	if ( count( $stats > 0 ) ) {		?>          <div class="statistics_table">            <table>                <thead>                    <tr>                        <th>                            Date-Time                        </th>                        <th>                            Status                        </th>                        <th>                            Details                        </th>                    </tr>                </thead>                <tbody>                    <?php					foreach ( $stats as $event ) {						$status = '';						if ( $event->monitor_type == '-1' ) {							$type = $event->type;							$status = ucfirst( $monitor_statuses[ $type ] );						} else {							$type = $event->monitor_type;							switch ( $type ) {								case '0':									$status = 'Paused';									break;								case '1':									$status = 'Started';							}						}						?>                        <tr>                            <td>                                <?php echo date( 'd/m/Y H:i:s', strtotime( $event->event_datetime_gmt ) ) ?>                            </td>                            <td class="<?php echo strtolower( $status ) ?>">                                <?php								echo $status;								?>                            </td>                            <td>                                <?php								switch ( $status ) {									case 'Started':										echo 'The monitor is started manually';										break;									case 'Up':										echo 'Successful response received.';										break;									case 'Paused':										echo 'The monitor is paused manually';										break;									case 'Down':										if ( $url->url_monitor_type == '2' ) {											echo 'The keyword exists.'; } else {											echo 'No Response From The Website.'; }										break;								}								?>                                      </td>                        </tr>                        <?php					}					?>                      </tbody>                <tfoot>                    <tr>                        <td align=left>                            <?php							if ( $stats_page > 1 ) {								?>                                              <a href="<?php echo home_url() ?>/wp-admin/admin-ajax.php?action=admin_uptime_monitors_statistics_table&url_id=<?php echo intval( $url->url_id ) ?>&title=<?php echo urlencode( $title ) ?>&stats_page=<?php echo (int) $stats_page - 1 ?>&wp_nonce=<?php echo wp_create_nonce( AdminUptimeMonitorsController::$nonce_token . 'statistics_table' ); ?>" target="aum_popup">prev</a>                                <?php							}							?>                                          </td>                        <td>                        </td>                        <td align=right>                            <?php							if ( $stats_page < ceil( $stats_cnt / 10 ) ) {								?>                                              <a href="<?php echo home_url() ?>/wp-admin/admin-ajax.php?action=admin_uptime_monitors_statistics_table&url_id=<?php echo intval( $url->url_id ) ?>&title=<?php echo urlencode( $title ) ?>&stats_page=<?php echo (int) $stats_page + 1 ?>&wp_nonce=<?php echo wp_create_nonce( AdminUptimeMonitorsController::$nonce_token . 'statistics_table' ); ?>" target="aum_popup">next</a>                                             <?php							}							?>                                          </td>                    </tr>                <tfoot>            </table>        </div>        <?php	}	?></fieldset> <a href="#" class="close_link"><img src="<?php echo plugins_url( 'images/close.png', __FILE__ ) ?>" /></a><script type="text/javascript" src="https://ajax.googleapis.com/ajax/libs/jquery/1.6.0/jquery.min.js"></script><script>    jQuery(document).ready(function () {        jQuery('a.close_link').click(function () {            var el = window.frameElement;            el.parentNode.removeChild(el);        })    });</script>